import React, { useState, useEffect } from 'react';
import { initialProducts, initialUsers, initialOrders } from './data';
import { User, Order, PaymentAccountDetails, ProductsData } from './types';
import AuthPage from './Auth';
import Dashboard from './Dashboard';
import ProductFlow from './ProductFlow';
import BuyCreditsView from './BuyCreditsView';
import MyOrdersView from './MyOrdersView';
import AdminPanel from './AdminPanel';
import FAQView from './FAQView';
import UserProfileView from './UserProfileView';
import { useLanguage } from './i18n';
import { LoadingSpinner } from './components';
import { usePersistentState } from './utils';
import api from './src/api';

const initialPaymentDetails: PaymentAccountDetails = {
    'KPay': { name: 'ATOM Point Admin', number: '09 987 654 321' },
    'Wave Pay': { name: 'ATOM Point Services', number: '09 123 456 789' }
};

const App = () => {
  const { t } = useLanguage();
  // State Management
  const [isLoading, setIsLoading] = useState(true);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState<User | null>(null);
  const [currentView, setCurrentView] = useState('DASHBOARD');

  // Persisted "Database" State - NOW USING API BACKEND FOR USERS
  const [products, setProducts] = usePersistentState<ProductsData>('app_products', initialProducts);
  // Note: users state removed - now managed by backend API
  const [orders, setOrders] = usePersistentState<Order[]>('app_orders', initialOrders);

  // Persisted Site-wide editable settings
  const [paymentDetails, setPaymentDetails] = usePersistentState<PaymentAccountDetails>('app_paymentDetails', initialPaymentDetails);
  const [adminContact, setAdminContact] = usePersistentState<string>('app_adminContact', 'https://t.me/CEO_METAVERSE');

  // Effect to restore session on initial load - NOW USING API
  useEffect(() => {
    const checkAuthSession = async () => {
      try {
        // Try to get current user from backend (checks JWT cookie)
        const response = await api.getCurrentUser();

        if (response.data && typeof response.data === 'object' && 'user' in response.data) {
          setCurrentUser((response.data as any).user);
          setIsLoggedIn(true);

          // Restore view from sessionStorage
          const storedView = sessionStorage.getItem('currentView');
          setCurrentView(storedView || 'DASHBOARD');
        }
      } catch (error) {
        console.log("No active session found");
        // Clear any old session storage
        sessionStorage.clear();
      } finally {
        setIsLoading(false);
      }
    };

    checkAuthSession();
  }, []);


  // Effect to manage body class for dynamic backgrounds
  useEffect(() => {
    const className = isLoggedIn ? 'dashboard-background' : 'auth-background';
    document.documentElement.className = className;
    document.body.className = className;

    // Cleanup on component unmount
    return () => {
      document.documentElement.className = '';
      document.body.className = '';
    };
  }, [isLoggedIn]);


  // Note: Removed localStorage users dependency - now using API-based authentication
  // Users are managed by the backend database, not frontend state

  const handleLoginSuccess = async (username: string, password?: string) => {
    // NOW USING API BACKEND
    if (!password) return;

    try {
      const response = await api.login(username, password);

      if (response.error) {
        if (response.error.includes('banned')) {
          alert(t('app.alerts.accountBanned'));
        } else {
          alert(t('app.alerts.invalidCredentials'));
        }
        return;
      }

      if (response.data && typeof response.data === 'object' && 'user' in response.data) {
        setCurrentUser((response.data as any).user);
        setIsLoggedIn(true);
        setCurrentView('DASHBOARD');
        sessionStorage.setItem('currentView', 'DASHBOARD');

        console.log('üéâ LOGIN SUCCESS - Data now stored in DATABASE, not localStorage!');
      }
    } catch (error) {
      console.error('Login error:', error);
      alert(t('app.alerts.invalidCredentials'));
    }
  };

  const handleRegisterSuccess = async (username: string, password: string, securityAmount: number) => {
    // NOW USING API BACKEND  
    try {
      const response = await api.register(username, password, securityAmount);

      if (response.error) {
        if (response.error.includes('banned')) {
          alert(t('app.alerts.usernameBanned'));
        } else if (response.error.includes('taken')) {
          alert(t('app.alerts.usernameTaken'));
        } else {
          alert('Registration failed: ' + response.error);
        }
        return;
      }

      if (response.data && typeof response.data === 'object' && 'user' in response.data) {
        const user = (response.data as any).user;
        setCurrentUser(user);
        setIsLoggedIn(true);
        setCurrentView('DASHBOARD');
        sessionStorage.setItem('currentView', 'DASHBOARD');

        alert(t('app.alerts.registrationSuccess', { id: user.id }));
        console.log('üéâ REGISTRATION SUCCESS - Data now stored in DATABASE, not localStorage!');
      }
    } catch (error) {
      console.error('Registration error:', error);
      alert('Registration failed. Please try again.');
    }
  };

  const handlePasswordReset = async (userId: number, newPassword: string) => {
    // NOW USING API BACKEND
    try {
      await api.resetPassword(userId, newPassword);
      alert('Password reset successful!');
    } catch (error) {
      console.error('Password reset error:', error);
      alert('Password reset failed. Please try again.');
    }
  };

  const handleLogout = async () => {
    // NOW USING API BACKEND
    try {
      await api.logout();
    } catch (error) {
      console.error('Logout error:', error);
    }

    setCurrentUser(null);
    setIsLoggedIn(false);
    sessionStorage.removeItem('currentView');
    console.log('üö™ LOGOUT - Session cleared from backend and frontend');
  };

  const handleBroadcast = async (message: string, targetIds: number[]) => {
      // NOW USING API BACKEND - notifications will be handled by backend
      try {
        // For now, show success message - backend will handle notifications
        alert(t('app.alerts.broadcastSent', { count: targetIds.length }));
      } catch (error) {
        console.error('Broadcast error:', error);
        alert('Broadcast failed. Please try again.');
      }
  };

  const sendAdminNotification = async (message: string) => {
    // NOW USING API BACKEND - admin notifications handled by backend
    try {
      // For now, just log the notification - backend will handle storage
      console.log('Admin notification sent:', message);
    } catch (error) {
      console.error('Admin notification error:', error);
    }
  };

  const navigateTo = (view: string) => {
    setCurrentView(view);
    sessionStorage.setItem('currentView', view);
  };

  // --- View Renderer --- //
  const renderView = () => {
    if (!currentUser) return null; // Should not happen if logged in
    switch (currentView) {
      case 'DASHBOARD':
        return <Dashboard user={currentUser} onNavigate={navigateTo} onLogout={handleLogout} adminContact={adminContact} />;
      case 'BROWSE_PRODUCTS':
        return <ProductFlow products={products} onNavigate={navigateTo} user={currentUser} setOrders={setOrders} onAdminNotify={sendAdminNotification} />;
      case 'BUY_CREDITS':
        return <BuyCreditsView user={currentUser} onNavigate={navigateTo} setOrders={setOrders} onAdminNotify={sendAdminNotification} paymentAccountDetails={paymentDetails} />;
      case 'MY_ORDERS':
        return <MyOrdersView user={currentUser} onNavigate={navigateTo} orders={orders} />;
      case 'ADMIN_PANEL':
        return <AdminPanel 
                    onNavigate={navigateTo} 
                    products={products} setProducts={setProducts} 
                    orders={orders} setOrders={setOrders}
                    onBroadcast={handleBroadcast}
                    currentUser={currentUser}
                    onLogout={handleLogout}
                    paymentDetails={paymentDetails}
                    setPaymentDetails={setPaymentDetails}
                    adminContact={adminContact}
                    setAdminContact={setAdminContact}
                />;
       case 'FAQ':
        return <FAQView onNavigate={navigateTo} />;
      case 'USER_PROFILE':
        return <UserProfileView user={currentUser} orders={orders} onNavigate={navigateTo} />;
      default:
        return <Dashboard user={currentUser} onNavigate={navigateTo} onLogout={handleLogout} adminContact={adminContact} />;
    }
  };

  if (isLoading) {
    return <LoadingSpinner />;
  }

  return (
    <>
      {isLoggedIn && currentUser ? (
        renderView()
      ) : (
        <AuthPage 
            onLoginSuccess={handleLoginSuccess} 
            onRegisterSuccess={handleRegisterSuccess}
            onPasswordReset={handlePasswordReset}
            adminContact={adminContact}
        />
      )}
    </>
  );
};

export default App;

frontend/Auth.tsx.content:
import React, { useState } from 'react';
import { Logo } from './components';
import { User } from './types';
import { useLanguage } from './i18n';

// --- AUTHENTICATION COMPONENTS --- //

const HelpIcon = () => (
    <svg viewBox="0 0 24 24" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
        <path d="M0 0h24v24H0z" fill="none"/>
        <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-6h2v6zm0-8h-2V7h2v2z"/>
    </svg>
);


interface AuthProps {
    onLoginSuccess: (username: string, password?: string) => void;
    onRegisterSuccess: (username: string, password: string, securityAmount: number) => void;
    onPasswordReset: (userId: number, newPassword: string) => void;
    adminContact: string;
}

const AuthPage = ({ onLoginSuccess, onRegisterSuccess, onPasswordReset, adminContact }: AuthProps) => {
  const [isLoginView, setIsLoginView] = useState(true);
  const { t } = useLanguage();
  const toggleView = () => setIsLoginView(!isLoginView);

  return (
    <div className="auth-container">
      <Logo />
      <div className="auth-form-wrapper">
        {isLoginView ? (
          <LoginForm onToggleView={toggleView} onLoginSuccess={onLoginSuccess} onPasswordReset={onPasswordReset} />
        ) : (
          <RegisterForm onToggleView={toggleView} onRegisterSuccess={onRegisterSuccess} />
        )}
        <a href={adminContact} target="_blank" rel="noopener noreferrer" className="contact-admin-link">
            <HelpIcon />
            <span>{t('auth.contactAdmin')}</span>
        </a>
      </div>
    </div>
  );
};

interface ForgotPasswordModalProps {
    onClose: () => void;
    onPasswordReset: (userId: number, newPassword: string) => void;
}

const ForgotPasswordModal: React.FC<ForgotPasswordModalProps> = ({ onClose, onPasswordReset }) => {
    const { t } = useLanguage();
    const [step, setStep] = useState<'verify' | 'reset'>('verify');
    const [userIdInput, setUserIdInput] = useState('');
    const [securityAmountInput, setSecurityAmountInput] = useState('');
    const [verifiedUser, setVerifiedUser] = useState<User | null>(null);

    const [newPassword, setNewPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

    const handleVerifySubmit = (e: React.FormEvent) => {
        e.preventDefault();
        const id = parseInt(userIdInput, 10);
        const amount = parseInt(securityAmountInput, 10);

        if (isNaN(id) || isNaN(amount)) {
            alert(t('auth.forgotPasswordModal.alerts.invalidInput'));
            return;
        }

        // For now, create a simplified user object for password reset
        // In production, this would verify against the backend API
        const user = { id, securityAmount: amount, username: `user${id}` };
        setVerifiedUser(user as User);
        setStep('reset');
    };

    const handleResetSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (!verifiedUser) return;

        if (!newPassword || newPassword.length < 6) {
             alert(t('auth.resetPasswordModal.alerts.passwordTooShort'));
             return;
        }

        if (newPassword !== confirmPassword) {
            alert(t('auth.resetPasswordModal.alerts.passwordsDontMatch'));
            return;
        }

        onPasswordReset(verifiedUser.id, newPassword);
        alert(t('auth.resetPasswordModal.alerts.passwordResetSuccess', { username: verifiedUser.username }));
        onClose();
    };


    return (
        <div className="modal-backdrop">
            <div className="modal-content">
                {step === 'verify' ? (
                <>
                    <h3>{t('auth.forgotPasswordModal.title')}</h3>
                    <p>{t('auth.forgotPasswordModal.description')}</p>
                    <form onSubmit={handleVerifySubmit} className="auth-form">
                        <div className="input-group">
                            <label htmlFor="recovery-userid">{t('auth.forgotPasswordModal.userIdLabel')}</label>
                            <input id="recovery-userid" className="input-field" type="number" value={userIdInput} onChange={e => setUserIdInput(e.target.value)} placeholder={t('auth.forgotPasswordModal.userIdPlaceholder')} required />
                        </div>
                         <div className="input-group">
                            <label htmlFor="recovery-amount">{t('auth.forgotPasswordModal.securityAmountLabel')}</label>
                            <input id="recovery-amount" className="input-field" type="number" value={securityAmountInput} onChange={e => setSecurityAmountInput(e.target.value)} placeholder={t('auth.forgotPasswordModal.securityAmountPlaceholder')} required />
                        </div>
                        <div className="modal-actions">
                            <button type="button" onClick={onClose} className="button-secondary">{t('common.cancel')}</button>
                            <button type="submit" className="submit-button">{t('auth.forgotPasswordModal.verifyButton')}</button>
                        </div>
                    </form>
                </>
                ) : verifiedUser && (
                <>
                    <h3>{t('auth.resetPasswordModal.title')}</h3>
                    <p>{t('auth.resetPasswordModal.description', { username: verifiedUser.username })}</p>
                    <form onSubmit={handleResetSubmit} className="auth-form">
                        <div className="input-group">
                            <label htmlFor="reset-new-password">{t('auth.resetPasswordModal.newPasswordLabel')}</label>
                            <input id="reset-new-password" className="input-field" type="password" value={newPassword} onChange={e => setNewPassword(e.target.value)} placeholder={t('auth.resetPasswordModal.newPasswordPlaceholder')} required />
                        </div>
                         <div className="input-group">
                            <label htmlFor="reset-confirm-password">{t('auth.resetPasswordModal.confirmPasswordLabel')}</label>
                            <input id="reset-confirm-password" className="input-field" type="password" value={confirmPassword} onChange={e => setConfirmPassword(e.target.value)} required />
                        </div>
                        <div className="modal-actions">
                            <button type="button" onClick={onClose} className="button-secondary">{t('common.cancel')}</button>
                            <button type="submit" className="submit-button">{t('auth.resetPasswordModal.setNewPasswordButton')}</button>
                        </div>
                    </form>
                </>
                )}
            </div>
        </div>
    );
};

interface LoginFormProps {
    onToggleView: () => void;
    onLoginSuccess: (username: string, password?: string) => void;
    onPasswordReset: (userId: number, newPassword: string) => void;
}

const LoginForm = ({ onToggleView, onLoginSuccess, onPasswordReset }: LoginFormProps) => {
  const { t } = useLanguage();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showForgotPassword, setShowForgotPassword] = useState(false);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onLoginSuccess(username, password);
  };

  return (
    <>
      <h1>{t('auth.signInTitle')}</h1>
      <form className="auth-form" onSubmit={handleSubmit}>
         <div className="input-group">
          <label htmlFor="login-username">{t('auth.usernameLabel')}</label>
          <input id="login-username" className="input-field" type="text" value={username} onChange={(e) => setUsername(e.target.value)} required aria-label={t('auth.usernameLabel')} />
        </div>
        <div className="input-group">
          <label htmlFor="login-password">{t('auth.passwordLabel')}</label>
          <input id="login-password" className="input-field" type="password" value={password} onChange={(e) => setPassword(e.target.value)} required aria-label={t('auth.passwordLabel')} />
        </div>
        <div className="forgot-password-container">
            <button
                type="button"
                className="forgot-password-link"
                onClick={() => setShowForgotPassword(true)}
            >
                {t('auth.forgotPasswordLink')}
            </button>
        </div>
        <button type="submit" className="submit-button">{t('auth.loginButton')}</button>
      </form>
      <div className="toggle-form-container">
        <span>{t('auth.registerPrompt')} </span>
        <button onClick={onToggleView} className="toggle-form-link" aria-label="Switch to registration form">{t('auth.registerLink')}</button>
      </div>
      {showForgotPassword && <ForgotPasswordModal onClose={() => setShowForgotPassword(false)} onPasswordReset={onPasswordReset} />}
    </>
  );
};

interface RegisterFormProps {
    onToggleView: () => void;
    onRegisterSuccess: (username: string, password: string, securityAmount: number) => void;
}

const RegisterForm = ({ onToggleView, onRegisterSuccess }: RegisterFormProps) => {
  const { t } = useLanguage();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [securityAmount, setSecurityAmount] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      alert(t('auth.resetPasswordModal.alerts.passwordsDontMatch'));
      return;
    }
    const amount = parseInt(securityAmount, 10);
    if (isNaN(amount) || securityAmount.length !== 4) {
        alert(t('auth.forgotPasswordModal.alerts.invalidInput')); // Re-using a similar alert
        return;
    }
    onRegisterSuccess(username, password, amount);
  };

  return (
    <>
      <h1>{t('auth.createAccountTitle')}</h1>
      <form className="auth-form" onSubmit={handleSubmit}>
        <div className="input-group">
          <label htmlFor="register-username">{t('auth.usernameLabel')}</label>
          <input id="register-username" className="input-field" type="text" value={username} onChange={(e) => setUsername(e.target.value)} required aria-label={t('auth.usernameLabel')} />
        </div>
        <div className="input-group">
          <label htmlFor="register-password">{t('auth.passwordLabel')}</label>
          <input id="register-password" className="input-field" type="password" value={password} onChange={(e) => setPassword(e.target.value)} required aria-label={t('auth.passwordLabel')} />
        </div>
        <div className="input-group">
          <label htmlFor="register-confirm-password">{t('auth.confirmPasswordLabel')}</label>
