if (container) {
    const root = createRoot(container);
    root.render(
      <LanguageProvider>
        <NotificationProvider>
          <App />
        </NotificationProvider>
      </LanguageProvider>
    );
} else {
    console.error("Fatal Error: Root container 'root' not found in the DOM.");
}

